import { useContext } from 'react';
import { TodoContext } from '../contexts/ToDoContext';

const Label = ({ todo }) => {
  const { updateTodo, deleteTodo } = useContext(TodoContext);
  return (
    <h3>
      <label
        className={todo.completed == 1 ? 'completed' : null}
        onClick={() => {
          updateTodo(todo.id);
        }}
      >
        {todo.todoname}
      </label>
      <label
        onClick={() => {
          deleteTodo(todo.id);
        }}
      >
        &nbsp;&nbsp;삭제
      </label>
    </h3>
  );
};

export default Label;
