import { useEffect, useRef } from 'react';

const Input = () => {
  const inputRef = useRef('');
  useEffect(() => {
    inputRef.current.focus();
  });
  return (
    <form onSubmit={insertTodo}>
      <input
        type='text'
        required={true}
        value={input}
        onChange={handleChangeText}
        ref={inputRef}
      />
      <input type='submit' value='Create' />
    </form>
  );
};
export default Input;
